import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getProductsBySlug,getProductPage } from "../../../actions";
import getParams from "../../../utils/getParams";
import { generatePublicUrl } from "../../../urlConfig";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from "react-responsive-carousel";
import Price from "../../../components/UI/Price";
import Rating from "../../../components/UI/Rating";
import {Link} from 'react-router-dom';
import "./style.css";

export default function ProductStore(props) {
    const dispatch = useDispatch();
  const product = useSelector((state) => state.product);
  const { page } = product;
  console.log(product);
  const slug = props.match.params.slug
  console.log(props);
  const [priceRange,setPriceRanger] = useState({
      under5k: 5000000,
      under10k: 10000000,
      under20k: 20000000,
      under50k: 50000000
  });
  const params = getParams(props.location.search);
  console.log(params)
  useEffect(() => {
    const params = getParams(props.location.search);
    const payload = {
      params,
    };
    dispatch(getProductPage(payload));
    const { match } = props;
    dispatch(getProductsBySlug(match.params.slug));
  }, []);
  const url = (e) => {
    return `http://localhost:2000${e}`;
  };
    return (
        <>
        
          {page.banners &&
          page.banners.map((banner, index) => (
            <a
              key={index}
              style={{ display: "block" }}
              href={banner.navigateTo}
            >
              <img style={{width: '1500px', height: '300px',margin:'50px'}} src={url(banner.img)} alt="" />
            </a>
          ))}
          <div style={{
          display: 'flex',
          justifyContent: "center",}}><p></p>{page.title}</div>
        {Object.keys(product.productsByPrice).map((key, index) => {
        return (
            <div>
              
            <div style={{display: 'flex',justifyContent: "center",}}>
              {product.productsByPrice[key].map((product) => (
                <Link 
                to={`/${product.slug}/${product._id}/p`}
                style ={{
                  display: 'block',
                  textDecoration: "none",
                    color: "#000",
                }} className="productContainer">
                  <div className="productImgContainer">
                    <img src={generatePublicUrl(product.productPictures[0].img)} alt="" />
                  </div>
                  <div className="productInfo">
                    <div style={{ margin: "5px 0" }}>{product.name}</div>
                    <div>
                    <Rating value="4.3" />
                      &nbsp;&nbsp;
                      <span
                        style={{
                          color: "#777",
                          fontWeight: "500",
                          fontSize: "12px",
                        }}
                      >
                        (3353)
                      </span>
                    </div>
                    <Price value={product.price} />
                  </div>
                </Link>
              ))}
            </div>
            </div>
        );
      })}
        </>
    )
}
