import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { getAllProduct,getAllCategory } from '../../actions';
import Layout from '../../components/Layout'


export default function HomePage() {
    // const product = useSelector(state=>state.product);
    // const dispatch = useDispatch();
    // useEffect(()=>{
    //     dispatch(getAllProduct());
    // },[]);
    // const renderProducts = (products) =>{
    //     let myProducts =[];
    //     for (let product of products){
    //         <h6>{product.name}</h6>
    //         myProducts.push(
    //             <li key={product.name}>
    //                 {product.name}
    //                 {product.products.length >0 ? 
    //                     <ul>{product.name}</ul>
    //                 :<span>{product.name}</span>}
                    
                    
    //             </li>
                
    //         )
    //     }
    //     return myProducts;
    // };
    const category = useSelector(state => state.category);

    useEffect(()=>{
        dispatch(getAllCategory());
    },[]);
    const renderCategories = (categories) => {
        let myCategories = [];
        for (let category of categories) {
          myCategories.push(
            <li key={category.name}>
                {
                category.parentId ? <a href={category.slug}>{category.name}</a>:
                <span><a href={category.slug}>{category.name}</a></span>
                }
              {category.children.length > 0 ? (
                <ul>{renderCategories(category.children)}</ul>
              ) : null}
              
            </li>
          );
        }
    }
    return (
        <Layout>
            <ul>
                
            {category.categories.length > 0 ? renderCategories(category.categories) : null}</ul>
            <h6>123</h6>
        </Layout>
    )
}
