import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getAllCategory,getAllProduct } from "../../actions";
import "./style.css";

export default function MenuHeader() {
    const category = useSelector(state => state.category);
    const dispatch = useDispatch();

    useEffect(()=>{
        dispatch(getAllCategory());
        dispatch(getAllProduct());
    },[]);

  const renderCategories = (categories) => {
    let myCategories = [];
    for (let category of categories) {
      myCategories.push(
        <li key={category.name}>
            {
            category.parentId ? <a href={category.slug}>{category.name}</a>:
            <span><a href={category.slug}>{category.name}</a></span>
            }
          {category.children.length > 0 ? (
            <ul>{renderCategories(category.children)}</ul>
          ) : null}
          
        </li>
      );
    }
    return myCategories;
  };
  const product = useSelector(state=>state.product);
    useEffect(()=>{
        dispatch(getAllProduct());
    },[]);
    const renderProducts = (products) =>{
        let myProducts =[];
        for (let product of products){
            myProducts.push(
                <li key={product.name}>
                    {product.parentId ? (
                        <ul>{product.name}</ul>
                    ):<span>{product.name}</span>}
                    <h6>{product.name}</h6>
                </li>
            )
        }
        return myProducts;
    }
  return <div className="menuHeader">
      <ul>
          {category.categories.length > 0 ? renderCategories(category.categories) : null}
          {product.products.length>0? renderProducts(product.products):null}
      </ul>
      
  </div>;
}
